package myrandom

type GetRandomBytesArgs struct {
	// The number of random bytes to generate.
	Count uint `pulumi:"count"`
}

type GetRandomBytesResult struct {
	// The generated bytes.
	Bytes []byte `pulumi:"bytes"`
}

// GetRandomBytes resource generates a number of random bytes. The byte contents will change from
// run to run depending on the provider's seed.
//
//pulumi:function
func GetRandomBytes(ctx *provider.Context, p *Provider, args *GetRandomBytesArgs, options provider.CallOptions) (*GetRandomBytesResult, error) {
	return &GetRandomBytesResult{
		Bytes: p.randomBytes(int(args.Count)),
	}, nil
}
